import React from 'react'

const Sizes = ({selectedSizes, setSize}) =>  {

    const sizingList = ['S', 'M', 'L', '1Size'];

    return (
        <div>
            <h4>Filter By Size:</h4>
            <div>
                {
                    sizingList.map((s, i) => {
                        return (
                            <button 
                                className={ "size" + (selectedSizes.includes(s) ? " selected-size" : "")}
                                key={i}
                                onClick={() => setSize(s)}
                            >
                                {s}
                            </button>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Sizes;
