import prods from '../products.json'

export default function filterList(array1, filterMechanism) {

    if(filterMechanism == null) return prods;
    else {
          return prods.filter(p => {
          const sizeA = p.size.split(" ");
            if(sizeA.some(r => array1.indexOf(r) >= 0)) {
                return p;
          }
        //   else {
        //       return prods;
        //   }
      })  
    }
}
