import React, { useState, useEffect } from 'react';
import ItemCart from './ItemCart';

const ShoppingCart = ({prods, changeQuantity}) => {
    const [total, setTotal] = useState(0);
    useEffect(() => {
        let tot = 0;
        for(var i = 0; i < prods.length ; i++) {
            tot+= prods[i].price*prods[i].quantity;
        }
        setTotal(tot);
    }, [prods])

    return (
        <React.Fragment>    
                    <h3>
                        Your Cart
                    </h3> 
                        {prods.length === 0 
                            ? 
                            <div>
                                <p>You Have No Items to Checkout. Please add!</p>
                            </div> 
                            :
                            prods.map(product => {
                                return (
                                    <ItemCart 
                                        key={product.id} 
                                        product={product} 
                                        changeQuantity={changeQuantity} 
                                         />)
                            })
                        } <h3> Grand Total </h3>
                        <p>$ {total.toFixed(2)}</p>
        </React.Fragment>
    )
}

export default ShoppingCart;
