import React, {useState} from 'react';

import ProdList from './ProdList';

const Filtering = ({elements, sortEl, addToElement}) =>  {

    const [e, setE] = useState('Select');

    const settingElements = (e) => {
        setE(e.target.value);
        sortEl(e.target.value);
    }

    return (
        <div>
            <div >
                <h3>Clothing Products and Accessories</h3>
                <div>
                    Sort by Price:
                    <select e={e} onChange={settingElements}>
                        <option e="Select">Select</option>
                        <option e="Highest to Lowest">Highest to Lowest</option>
                        <option e="Lowest to Highest">Lowest to Highest</option>
                    </select>
                </div>
            </div>

            <ProdList elements={elements} addTo={addToElement} />

        </div>
    )
}

export default Filtering;
