import React from 'react'

const Sizes = ({selectedSizes, setSize}) =>  {

    const sizes = ['S', 'M', 'L', '1Size'];

    return (
        <div className="sizes">
            <h3>Sizes</h3>
            <div>
                {
                    sizes.map((size, index) => {
                        return (
                            <button 
                                className={ "size" + (selectedSizes.includes(size) ? " selected-size" : "")}
                                key={index}
                                onClick={() => setSize(size)}
                            >
                                {size}
                            </button>
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Sizes;
