import products from '../products.json'

export default function filterList(array1, filterMechanism) {

    if(filterMechanism != null) {
          return products.filter(product => {
          const lenArray = product.size.split(" ");
          if(lenArray.length > 0) {
                if(sizeArray.some(r => array1.indexOf(r) >= 0)) {
                    return product;
            }
          }
          else {
              return products;
          }
      })  
    } else {
        return products;
    }
}
